import React, { useState, useEffect } from "react";
import balloon from "../images/balloon.png"; // your transparent PNG
import "./App.css";

const BalloonStream = () => {
  const [balloons, setBalloons] = useState([]);

  useEffect(() => {
    const interval = setInterval(() => {
      setBalloons(prev => [
        ...prev,
        { id: Date.now() } // unique id for each balloon
      ]);
    }, 1000); // spawn a new balloon every 1 second

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="balloons-container">
      {balloons.map(b => (
        <img
          key={b.id}
          src={balloon}
          alt="Balloon"
          className="balloon"
          style={{ animationDuration: `${5 + Math.random() * 3}s` }} // random speed
        />
      ))}
    </div>
  );
};

export default BalloonStream;
