import React, { useEffect, useState } from "react";
import BalloonMap from "./components/BalloonMap";

const App: React.FC = () => {
  const [balloons, setBalloons] = useState<{ lat: number; lon: number; name?: string }[]>([]);
  const [rand, setRand] = useState(0);

  useEffect(() => {
    const r = Math.floor(Math.random() * 1000);
    setRand(r);
    console.log(r);
  }, []);

  useEffect(() => {
    const fetchBalloons = async () => {
      try {
        const res = await fetch(`/api/treasure/get/00`);
        if (!res.ok) throw new Error("Failed to fetch data");

        const json = await res.json();

        // convert API data to {lat, lon} format
        const mapped = json.map((b: any) => ({
          lat: b[0],
          lon: b[1],
        }));
        console.log(mapped);

        setBalloons(mapped);
      } catch (err) {
        console.error(err);
      }
    };

    // only fetch if rand is set
    if (rand !== null) fetchBalloons();
  }, [rand]);

  return (
    <div>
      <BalloonMap balloons={balloons} />
    </div>
  );
};

export default App;
