import React, { useEffect, useState } from "react";
import Map from './components/Map';

const App: React.FC = () => {
  // const [balloons, setBalloons] = useState<{ lat: number; lon: number; alt: number }>({lat: 0, lon:0, alt:0});
  const [rand, setRand] =useState<number | null>(null);

  useEffect(() => {
    const r = Math.floor(Math.random() * 1000);
    setRand(r);
  }, []);

  // Only log when rand actually updates
  useEffect(() => {
    console.log("Random index chosen:", rand);
  }, [rand]);


  return (
    <div>
    <Map rand = {rand} />
    </div>
  );
};

export default App;
