import React from "react";
import { MapContainer, TileLayer, CircleMarker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";
import { LatLngExpression, LatLngBounds } from "leaflet";

const FitBounds: React.FC<{ balloons: Balloon[] }> = ({ balloons }) => {
  const map = useMap();
  if (balloons.length) {
    const bounds = new LatLngBounds(balloons.map(b => [b.lat, b.lon] as LatLngExpression));
    map.fitBounds(bounds, { padding: [50, 50] });
  }
  return null;
};

delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

type Balloon = {
  lat: number;
  lon: number;
  name?: string;
};

interface BalloonMapProps {
  balloons: Balloon[];
}

const BalloonMap: React.FC<BalloonMapProps> = ({ balloons }) => {
    console.log(balloons.length);
  return (
    <MapContainer
      center={balloons.length ? [balloons[0].lat, balloons[0].lon] : [0, 0]}
      zoom={4}
      style={{ height: "500px", width: "100%" }}
    >
    <FitBounds balloons={balloons} />
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
      />

      {balloons.map((b, idx) => (
        <CircleMarker
          key={idx}
          center={[b.lat, b.lon]}
          radius={5}             // small circle
          color="red"            // outline color
          fillColor="red"        // fill color
          fillOpacity={0.8}
        >
          <Popup>{b.name || `Balloon ${idx + 1}`}</Popup>
        </CircleMarker>
      ))}
    </MapContainer>
  );
};

export default BalloonMap;
