import React from "react";
import { MapContainer, TileLayer, CircleMarker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { LatLngExpression } from "leaflet";
import './BalloonMap.css'
// Define type
type Balloon = {
  lat: number;
  lon: number;
  alt: number;
};

const Recenter: React.FC<{ position: LatLngExpression }> = ({ position }) => {
  const map = useMap();
  map.setView(position, map.getZoom());
  return null;
};


const BalloonMap: React.FC<{ balloons: Balloon[] | null}> = ({ balloons }) => {
  if(!balloons) return null;
  console.log("Balloon received:", balloons);


  return (
    <MapContainer
      center={[0,0]}
      // center={[0, balloon.lon]}
      className="map"
      zoom={2}
      scrollWheelZoom={false}
    >
      {/* <FitBounds balloons={balloons} /> */}

      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
      />

      {balloons.map((balloon, index) => (
        <React.Fragment key={index}>
          <CircleMarker
            center={[balloon.lat, balloon.lon]}
            radius={5}
            color="red"
            fillColor="red"
            fillOpacity={0.8}
          >
            <Popup>{`Balloon ${index + 1} (Alt: ${balloon.alt} m)`}</Popup>
          </CircleMarker>

          {balloons.length > 1 && (<Recenter position={[balloon.lat, balloon.lon]} />)}
        </React.Fragment>
      ))}
    </MapContainer>
  );
};

export default BalloonMap;
