import React, { useEffect, useState } from "react";
import BalloonMap from "./components/BalloonMap";

const App: React.FC = () => {
  const [balloons, setBalloons] = useState<{ lat: number; lon: number; alt: number }>({lat: 0, lon:0, alt:0});
  const [rand, setRand] = useState(0);

  useEffect(() => {
    const r = Math.floor(Math.random() * 1000);
    setRand(r);
    console.log(r);
  }, []);

  useEffect(() => {
    const fetchBalloons = async () => {
      try {
        const file = '00';
        const res = await fetch(`/api/treasure/get/${file}`);
        if (!res.ok) throw new Error("Failed to fetch data");

        const json = await res.json();

        // convert API data to {lat, lon} format
        const mapped = json.map((b: any) => ({
          lat: b[0],
          lon: b[1],
          alt: b[2],
        }));
        console.log(mapped[rand]);

        setBalloons(mapped[rand]);
      } catch (err) {
        console.error(err);
      }
    };

    // only fetch if rand is set
    if (rand !== null) {
      console.log(rand);
      fetchBalloons();
    };
  }, [rand]);

  return (
    <div>
      <BalloonMap balloons={balloons} />
    </div>
  );
};

export default App;
