import React from "react";
import { MapContainer, TileLayer, CircleMarker, Popup, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L, { LatLngExpression, LatLngBounds } from "leaflet";

import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

// Fix default marker icons for Leaflet
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

// Define type
type Balloon = {
  lat: number;
  lon: number;
  alt: number;
};


const FitBounds: React.FC<{ balloons: Balloon[] }> = ({ balloons }) => {
  const map = useMap();

  if (balloons && balloons.length > 0) {
    // Create bounds that include all balloon coordinates
    const coords: LatLngExpression[] = balloons.map((b) => [b.lat, b.lon]);
    const bounds = new LatLngBounds(coords);
    map.fitBounds(bounds, { padding: [256, 256] });
  }

  return null;
};


const BalloonMap: React.FC<{ balloons: Balloon }> = ({ balloons }) => {
  console.log(balloons);

  return (
    <MapContainer
      center={[balloons.lat, balloons.lon]}
      zoom={15}
      // style={{ height: "100vh", width: "100vw" }}
      scrollWheelZoom={false}
    >
      {/* <FitBounds balloons={balloons} /> */}

      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
      />

        <CircleMarker
          key={balloons.alt}
          center={[balloons.lat, balloons.lon]}
          radius={5}
          color="red"
          fillColor="red"
          fillOpacity={0.8}
        >
          <Popup>{balloons.alt || `Balloon ${balloons.alt + 1}`}</Popup>
        </CircleMarker>
    </MapContainer>
  );
};

export default BalloonMap;
